SELECT t_componente_comp.ip AS IP,t_componente_comp.num_inventario AS Inventario,t_marca.descripcion AS Marca,t_modelo.descripcion AS Modelo,t_componente_comp.serial AS Serial,t_componente_comp.posicion AS Posicion,t_usuarios.nombre_emp AS Nombre,t_departamento.nombre AS Ubicacion FROM t_componente_comp INNER JOIN t_marca ON t_componente_comp.t_marca_id_marca = t_marca.id_marca INNER JOIN t_modelo ON t_componente_comp.t_modelo_id_modelo = t_modelo.id_modelo INNER JOIN t_usuarios ON t_componente_comp.t_usuarios_id_usuario = t_usuarios.id_usuario INNER JOIN t_departamento ON t_usuarios.t_departamento_id_depto = t_departamento.id_depto


